import React, { Component, useState, useEffect } from "react";
import "./App.css";

function App() {
    const [articulo, setArticulo] = useState([]);
    const [scrollDepth, setScrollDepth] = use(0);

    return (
        <div className="App">
            <h2>Scroll this far: {scrollDepth}</h2>

            <hr />

            <p>Items: {articulo.toString()}</p>
            <button
                onClick={() => {
                    setArticulo([...articulo, "manzana"]);
                }}
            >
                mas 1
            </button>
        </div>
    );
}

export default App;
